import "./app.css"
import {connect} from 'react-redux'
import {add_reminder,remove_reminder}from '../actions'

class App extends Component {
state={
    text:'',
    date:new Date()
}

renderReminders=()=>{
  const  remind=this.props.remind
remind.map(reminder=>{
  return(


    <div key={reminder.id} className="list-group-item">

        <div>{reminder.text}</div>
        <div>{reminder.date}</div>
        <span className='btn btn-danger' onClick={()=>this.props.remove_reminder(reminder.id)}>x</span>

    </div>
)
})

}






