import React, { Component } from 'react'
import "./app.css"
import {connect} from 'react-redux'
import {add_reminder,remove_reminder}from '../actions'

class App extends Component {
state={
    text:'',
    date:new Date()
}
    
    






    render() {
      

            const {remind}=this.props      
            const pops=remind.map(remind=>{
                return(
            <li key={remind.id} className="list-group-item">
                <div>{remind.text}</div>
                <div>{remind.date}</div>
                <div className='remove btn btn-danger' onClick={this.props.remove_reminder(remind)}></div>
            </li>
                )
            })
        
                
       
        return ( 
        <div className="app">
            <img src=" "/>
            <div className="title"><h2> what should i do</h2> </div>
            <input 
            onChange={(e)=>this.setState({text:e.target.value})}
            className="form-control"type="text" placeholder="enter reminder"/>
            <input 
            onChange={(e)=>this.setState({date:e.target.value})}
            className="form-control" type="datetime-local" placeholder="enter date" />
            <button
            onClick={()=>this.props.add_reminder(this.state.text,this.state.date)}
            className="btn btn-primary btn-block"
            >add reminder</button>
            {pops}
            <button className="btn btn-warning btn-block">clear reminder</button>

        </div>
    
        

        )
    }
}
// function mapDispatchToProps(dispatch){
//     return{
//         add_reminder:()=>dispatch(add_reminder())
// }
// }
export default connect ((state)=>{
    return{
     remind: state

    }
},{add_reminder,remove_reminder})(App)
