import React, { Component } from 'react'
import "./app.css"
import {connect} from 'react-redux'
import {add_reminder,remove_reminder,clear_reminder}from '../actions'

class App extends Component {
state={
    text:'',
    date:new Date()
}

renderReminders=()=>{
  const  remind=this.props.remind

  return(
<ul>
{
  remind.map(reminder=>{
return(
<li key={reminder.id}>
<div>{reminder.text}</div>
<div>{reminder.date}</div>
<div className=" remove btn btn-danger" onClick={()=>{this.props.remove_reminder(reminder.id)}}>delete</div>

</li>
)
  })
}
</ul>
  )
}





    render() {




        return (
        <div className="app">
            <img src=" "/>
            <div className="title"><h2> what should i do</h2> </div>
            <input
            onChange={(e)=>this.setState({text:e.target.value})}
            className="form-control"type="text" placeholder="enter reminder" value={this.state.text}/>
            <input
            onChange={(e)=>this.setState({date:e.target.value})}
            className="form-control" type="datetime-local" placeholder="enter date"value={this.state.text} />
            <button
            onClick={()=>{this.props.add_reminder(this.state.text,this.state.date)
            this.setState({
              text:"",  date:"" })
            }}
            className="btn btn-primary btn-block"
            >add reminder</button>
            {this.renderReminders()}
            <button 
             onClick={()=>this.props.clear_reminder()}
            className="btn btn-warning btn-block">clear reminder</button>

        </div>



        )
    }
}
// function mapDispatchToProps(dispatch){
//     return{
//         add_reminder:()=>dispatch(add_reminder())
// }
// }
export default connect ((state)=>{
    return{
     remind: state

    }
},{
    add_reminder,

    remove_reminder,
    clear_reminder
})(App)
