    date:new Date()
}

    render() {
        return ( 
        <div className="app">
            <img src=" "/>
            <div className="title"><h2> what should i do</h2> </div>
            <input 
            onChange={(e)=>this.setState()}
            className="form-control"type="text" placeholder="enter reminder"/>
            <input className="form-control" type="datetime-local" placeholder="enter date" />
            <button
            onClick={()=>add_reminder()}
            className="btn btn-primary btn-block"
            >add reminder</button>
            <button className="btn btn-warning btn-block">clear reminder</button>

        </div>
    
        

        )
    }
}
