import {Add_reminder} from '../types'
const state=[]
const reminder=(state,action=>{
let reminder=[]
if (action.type===Add_reminder){
    reminder=[...state,{ text:action,date:action.date,id:Math.random()}]
    console.log('loging',reminder)
    return reminder
}



    return state
})
export default reminder
